static void Test(int[] arr, int target, int expectedResult)
{
    var actualResult = Solution.ThreeSumMulti(arr, target);

    if (expectedResult != actualResult)
    {
        Console.ForegroundColor = ConsoleColor.Red;
        Console.WriteLine($"FAILED: {ToStr(arr)}; Target={target}; ExpectedResult={expectedResult}; ActualResult={actualResult}");
        Console.ResetColor();
    }
    else
    {
        Console.WriteLine($"SUCCESS: {ToStr(arr)}; Target={target}; Result={expectedResult}");
    }

    string ToStr(int[] arr) => string.Join(',', arr);
}



public static class Solution
{
    private const int ModuloArgument = 1_000_000_000 + 7;

    public static int ThreeSumMulti(int[] arr, int target)
    {
        if (arr is null || arr.Length < 3 || target < 0)
            return -1;

        return ThreeSumMulti_BruteForce(arr, target);
    }

    public static int ThreeSumMulti_BruteForce(int[] arr, int target)
    {
        Console.WriteLine(arr.Length);

        // O(N^3) time, O(1) space
        var result = 0;

        for (var i = 0; i < arr.Length - 2; i++)
        {
            for (var j = i + 1; j < arr.Length - 1; j++)
            {
                for (var k = j + 1; k < arr.Length; k++)
                {
                    if (arr[i] + arr[j] + arr[k] == target)
                    {
                        result++;
                    }
                }
            }
        }

        Console.WriteLine(result);

        return result % ModuloArgument;
    }

    public static int ThreeSumMulti_WithHashTable(int[] arr, int target)
    {
        Console.WriteLine(arr.Length);

        // O(N^2) time, O(N^2) space

        var sumOfJAndKToJAndKMapping = new Dictionary<int, List<(int j, int k)>>();
        

        for (var j = 1; j < arr.Length - 1; j++)
        {
            for (var k = j + 1; k < arr.Length; k++)
            {
                var sumOfJAndK = arr[j] + arr[k];

                if (sumOfJAndKToJAndKMapping.TryGetValue(sumOfJAndK, out var indices))
                    indices.Add((j, k));
                else
                    sumOfJAndKToJAndKMapping.Add(sumOfJAndK, new List<(int j, int l)> { (j, k) });
            }
        }

        var result = 0;
        for (var i = 0; i < arr.Length; i++)
        {
            var sumComplement = target - arr[i];
            if (sumOfJAndKToJAndKMapping.TryGetValue(sumComplement, out var indices))
            {
                foreach (var (j, _) in indices)
                {
                    if (i < j)
                        result++;
                }
            }
        }

        Console.WriteLine(result);

        return result % ModuloArgument;
    }
}